mapper.delete:
{ call del_mtrl_order(
    #{moCd, mode=IN, jdbcType=VARCHAR, javaType=String},
    #{modCd, mode=IN, jdbcType=VARCHAR, javaType=String}
   )
 }

mapper.update:
UPDATE MTRL_ORDER_DETAIL
   SET mo_cnt = #{moCnt},
       mo_req_dt = #{moReqDt}
WHERE  mo_cd = #{moCd}
  AND  cmm_cd = #{cmmCd}
