mapper.select:
SELECT SUM(ordr_dtl_cnt) as sum_dtl_cnt , d.cpr_cd as cpr_cd , p.cpr_nm as cpr_nm FROM order_sheet o , order_sheet_detail d , cm_product_mng p WHERE d.cpr_cd = p.cpr_cd(+) AND o.orsh_no = d.orsh_no AND d.orsh_pr = '접수완료' AND d.cpr_cd IN ('stick001','stick002','stick003','stick004','stick005') AND o.orsh_no IN
#{orshNo}
GROUP BY p.cpr_nm , d.cpr_cd ORDER BY d.cpr_cd

mapper.update:

	SELECT 'plan'||TO_CHAR(SYSDATE,'yyMMdd')||TRIM(TO_CHAR(prpl_seq.nextval,'099')) as prpl_cd
	  FROM DUAL

INSERT INTO proc_plan (prpl_cd,prpl_dt, prpl_mng)
VALUES(
		  #{prplCd}
		, TO_DATE(SYSDATE, 'rrrr-MM-dd')
		, #{prplMng}
	   )


           SELECT prpl_cd
			  FROM (
					SELECT prpl_cd
					  FROM proc_plan
					 ORDER BY 1 DESC
				   ) p
			 WHERE ROWNUM BETWEEN 1 AND 1

		INSERT INTO proc_plan_detail (prpld_cd, prpl_cd, cpr_cd, prpld_cnt)
		VALUES (
				   'plandtl'||TO_CHAR(SYSDATE,'yyMMdd')||TRIM(TO_CHAR(prpl_dtl_seq.nextval,'099'))
				 , #{prplCd}
				 , #{cprCd}
				 , #{prpldCnt}
			   )
