mapper.select:
SELECT SUM(ordr_dtl_cnt) as sum_dtl_cnt , d.cpr_cd as cpr_cd , p.cpr_nm as cpr_nm , o.orsh_no as orsh_no , d.ordr_dtl_cd as ordr_dtl_cd FROM order_sheet o , order_sheet_detail d , cm_product_mng p WHERE d.cpr_cd = p.cpr_cd(+) AND o.orsh_no = d.orsh_no AND d.orsh_pr = '접수완료' AND d.cpr_cd IN ('stick001','stick002','stick003','stick004','stick005') AND o.orsh_no IN
#{orshNo}
GROUP BY p.cpr_nm , d.cpr_cd , o.orsh_no , d.ordr_dtl_cd ORDER BY d.cpr_cd

mapper.update:

	SELECT 'plan'||TO_CHAR(SYSDATE,'yyMMdd')||TRIM(TO_CHAR(prpl_seq.nextval,'099')) as prpl_cd
	  FROM DUAL

INSERT INTO proc_plan (prpl_cd, orsh_no, prpl_status)
VALUES(
		  #{prplCd}
		, #{orshNo}
		, #{prplStatus}
	   )


	SELECT 'plandtl'||TO_CHAR(SYSDATE,'yyMMdd')||TRIM(TO_CHAR(prpl_dtl_seq.nextval,'099')) as prpld_cd
	  FROM DUAL

INSERT INTO proc_plan_detail (prpld_cd, prpld_work_tsk_pri, cpr_cd, prpld_cnt, prst_dt, prpld_mng, prpld_status, prpl_cd)
VALUES (
		   #{prpldCd}
		 , #{prpldWorkTskPri}
		 , #{cprCd}
		 , #{prpldCnt}
		 , #{prstDt}
		 , #{prpldMng}
		 , #{prpldStatus}
		 , #{prplCd}
	   )

mapper.select:
SELECT orsh_no , TO_CHAR(prpl_dt,'rrrr-MM-dd') as prpl_dt , prpl_status , cpr_nm , prpld_cnt , prpld_mng , prpld_status FROM proc_plan pp, proc_plan_detail ppd, cm_product_mng cpm WHERE pp.prpl_cd = ppd.prpl_cd AND ppd.cpr_cd = cpm.cpr_cd AND prpld_status = #{prpldStatus}
AND cpr_nm LIKE '%' || #{cprNm} || '%'
ORDER BY 1
