mapper.update:
UPDATE order_sheet_detail
   SET cpr_cd = (SELECT cpr_cd FROM cm_product_mng WHERE cpr_nm = #{cprNm}), ordr_dtl_cnt = #{ordrDtlCnt}
 WHERE ordr_dtl_cd = #{ordrDtlCd}

mapper.select:
SELECT sum(pls_cnt) sum_pls_cnt , cpm.cpr_cd , cpr_nm FROM cm_product_mng cpm , prod_lot_stock pls WHERE cpm.cpr_cd = pls.cpr_cd AND cpm.cpr_cd IN
#{cprCd}
GROUP BY cpm.cpr_cd, cpr_nm ORDER BY cpm.cpr_cd
